CREATE TABLE "profiles" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "user_id" integer, "name" varchar(255), "birthday" date, "bio" text, "color" varchar(255), "twitter" varchar(255), "created_at" datetime, "updated_at" datetime);
